import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { AccountCircle, WidthFull } from '@mui/icons-material';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import { Chip, Divider, Input } from '@mui/material';

export default function Post(params) {
    let content = params.data.content;
    const [open, setOpen] = React.useState(false);
    const [scroll, setScroll] = React.useState('paper');
  
    const handleClickOpen = (scrollType) => () => {
      setOpen(true);
      setScroll(scrollType);
    };
  
    const handleClose = () => {
      setOpen(false);
    };
  
    const descriptionElementRef = React.useRef(null);
    React.useEffect(() => {
      if (open) {
        const { current: descriptionElement } = descriptionElementRef;
        if (descriptionElement !== null) {
          descriptionElement.focus();
        }
      }
    }, [open]);
    return (
    <Card sx={{ minWidth: 200,maxWidth:200,minHeight:250,maxHeight:250,m:2,p:2 }}>
      <CardContent sx={{minHeight:180,maxHeight:180}}>
        <Typography variant="h5" component="div">
            <span style={{marginRight:"6px",paddingTop:"4px"}}>
                <AccountCircle></AccountCircle>
            </span>
            {params.data.name}
        </Typography>
        <Typography variant="body2" align='justify' paragraph sx={{mt:2}}>
            {content.substring(0,300)+"..."}
        </Typography>
      </CardContent>
      <CardActions >
        <Button  size="small" onClick={handleClickOpen('paper')}>Learn More</Button>
      </CardActions>
      <React.Fragment>
        <Dialog
            open={open}
            onClose={handleClose}
            scroll={scroll}
            aria-labelledby="scroll-dialog-title"
            aria-describedby="scroll-dialog-description" fullScreen
        >
            <DialogTitle id="scroll-dialog-title">{params.data.name}</DialogTitle>
            <DialogContent dividers={scroll === 'paper'}>
            <DialogContentText
                id="scroll-dialog-description"
                ref={descriptionElementRef}
                tabIndex={-1}
            >
                {content+params.data._id}
            </DialogContentText>
            <Divider>
                <Chip label="Comments" ></Chip>
            </Divider>
            <DialogContentText
                id="scroll-dialog-description"
                ref={descriptionElementRef}
                tabIndex={-1}
            >
            

            </DialogContentText>
            </DialogContent>
            <DialogActions>
            <Input multiline aria-label='comment' placeholder='Enter your comment' sx={{width:400}}></Input>
            <Button onClick={handleClose}>Cancel</Button>
            <Button onClick={handleClose}>Subscribe</Button>
            </DialogActions>
        </Dialog>
        </React.Fragment>
    </Card>
  );
}
